    public function actionLogin($nama, $pass) {

        $model = new \app\models\LoginForm();
        $model->username = $nama;
        $model->password = $pass;
        $model->load(Yii::$app->getRequest()->getBodyParams(), '');
        if ($model->login()) {
            $respon = Yii::$app->getResponse();
            $respon->setStatusCode(200);
        } else {
            $respon = Yii::$app->getResponse();
            $respon->setStatusCode(200);
        }
//         \yii\helpers\VarDumper::dump($model);
//        die();

        if (empty($model->login())) {
            return [
                'Result' => "False",
                'Raw' => 'Username atau Password salah'];
        } else {
            return [
                'Result' => "True",
                'Raw' => array(
                    [
                        'UserName' => Yii::$app->user->identity->username,
                        'Password' => $model->password,
                        'Nama' => Yii::$app->user->identity->nama,
                        //'pass' => \Yii::$app->getSecurity()->decryptByPassword(\Yii::$app->user->identity->password_hash),
                        'Email' => Yii::$app->user->identity->email,
                    ]
                )
            ];
        }
    }

    public function actionChangePassword($nik, $email, $newpass) {
        $model = $this->findModel($nik, $email);
        $model->password_hash = \Yii::$app->getSecurity()->generatePasswordHash($newpass);
        $respon = Yii::$app->getResponse();
        if ($model->save()) {
            $respon->setStatusCode(200);
            return [
