    public function actionCountingOpenTask($nik, $nama) {
        $model = (new \yii\db\Query())
                ->select('COUNT(task.status_task) as tot')
                ->from('task')
                ->leftJoin('user', 'task.user_id = user.id')
                ->where(['user.nik' => $nik])
                ->andWhere(['user.nama' => $nama])
                ->andWhere(['LIKE', 'task.status_task', 'open'])
                ->one();
        $respon = Yii::$app->getResponse();
        $respon->setStatusCode(200);
        return [
            'Result' => "True",
            'Raw' => array(
                [
                    'tot' => $model['tot'],
                    'Status' => 'Aktif'
                ]
            )
        ];
    }

