    public function actionListOpenTask($nik, $name) {
        $model = (new \yii\db\Query())
                ->select('t.id as ID,t.nama_remote as NAMAREMOTE, t.alamat as ALAMAT, t.no_task as NoTask,t.vid as VID,t.tanggal_task as TanggalTask,t.provinsi as PROVINSI,t.id_jenis_task as idJenisTask,t.nama_kordinator as NamaKordinator, u.nama as NamaTeknisi, t.status_task as StatusTask')
                ->from('task t')
                ->leftJoin('user u', 't.user_id = u.id')
                ->where(['u.nik' => $nik])
                ->andWhere(['u.nama' => $name])
                ->andWhere(['LIKE', 't.status_task', 'open'])
                ->all();
//            \yii\helpers\VarDumper::dump($model[0]['ID']);die;

        $respon = \Yii::$app->getResponse();
        if ($model != null) {
            $respon->setStatusCode(200);
            return [
                'Result' => "True",
                'Raw' =>
                $model
            ];
        } else {
            $respon->setStatusCode(200);
            return [
                'Result' => "False",
                'Raw' => 'Data untuk NIK ' . $nik . ' dan untuk NAMA ' . $name . ' tidak ditemukan'
            ];
        }
    }

